import React, { Component } from 'react';
import Square from './Square';


class Board extends Component {
    state = {  }


    getSquare =(i) =>{


        return <Square value={this.props.squares[i]}
        clicked={()=>this.props.clicked(i)}
        />
    }


    makeSquares = () =>{

for(let i=1;i<4;i++){

    return (
<div className="border-row">
{this.getSquare(0*i)}
{this.getSquare(1*i)}
{this.getSquare(2*i)}
</div>

    )
}

    }

    render() { 
        return (  
<React.Fragment>

{this.makeSquares}
</React.Fragment>
        );
    }
}

export default Board;
